context.flow.set('orderSuccess', false);
/*ordersuccess blir false og man kan bestille pizza på nytt
på vanlig måte*/
msg.payload = "Pizze delivery, bon apetito!"; 

//bilen sender melding om at den leverer/har levert
return msg; 
//returnerer meldingen

msg.payload = "Awaiting order..."
//setter payload til strengen Awaiting order...
return msg;
//returnerer meldingen

var totalIncome = context.flow.get('totalIncome') || 0; 
//mottar context totalIncome, hvis den ikke finnes lages den og verdien settes til 0
var price = context.flow.get('price') || 0;             
//mottar context price, hvis den ikke finnes lages den og verdien settes til 0

if (msg.payload > 10) { 
    price = msg.payload;
    //hvis payloaden den mottar er over 10, er payloaden prisen på det som blir kjøpt i dropdown menyen
    context.flow.set('price', price); 
    /*da settes prisen til det som ble valgt i en context
    slik at den kan brukes til å summere totalCost*/
} else if (msg.payload < 10) {
    totalIncome = totalIncome + price; 
    /*når paylpoaden er under 10 så har kunden trykket på husnummer
    og da legges prisen til summen*/
    context.flow.set('totalIncome', totalIncome); 
    //totalIncome blir lagret og kan brukes igjen når ny pizza blir bestilt

    return { payload: totalIncome }; 
    //returnerer totalIncome
}

var orderSuccess = context.flow.get('orderSuccess'); 
//henter price fra context
if(orderSuccess == true){ 
    /*hvis ordersucess er true har order success funksjonen kjørt, det 
    vil si at pizzaen er bestilt korrekt*/
    msg.payload = "Pizze on the way!"; 
    return msg; //returnerer strengen
}

if (msg.payload == "Awaiting order...") { 
    /*når bilen er fremme og har skutt ut pizza skal menyen 
    oppdateres til de gamle verdiene, og man kan kjøpe ny*/
    msg.options = [
        { '1. Pizze of the day - 16$':16 },
        { '2. Margherita - 15$': 15 },
        { '3. Bufalata - 14$': 14 },
        { '4. Vesuvio - 12$': 12 },   //lager nye valg i dropdown menyen
        { '5. Capricciosa - 17$': 17 },
        { '6. Calzone - 18$': 18 },
        { '7. Parma - 19$': 19 },
        { '8. Vegana - 20$': 20 },
    ];
    return msg; //returnerer meldingen
}

context.flow.get('orderSuccess') || false;
var optionSelected = context.flow.get('optionSelected'); 
//henter optionselected fra context.

if(optionSelected == true && msg.payload < 10){
/*hvis der er valgt en pizza og det er et knappetrykk
så er pizzaen riktig bestilt*/
